// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

import { AccessControlledV8 } from "@venusprotocol/governance-contracts/contracts/Governance/AccessControlledV8.sol";
import { ensureNonzeroAddress } from "@venusprotocol/solidity-utilities/contracts/validators.sol";
import { IVoteSyncReceiver } from "./IVotesSyncReceiver.sol";

/**
 * @title VotesSyncReceiverAdmin
 * @author Venus
 * @notice The VotesSyncReceiverAdmin contract extends a parent contract AccessControlledV8 for access control, and it manages contract called voteSyncReceiver.
 * It maintains a registry of function signatures and names, allowing for dynamic function handling i.e checking of access control of interaction with only owner functions.
 */
contract VotesSyncReceiverAdmin is AccessControlledV8 {
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    IVoteSyncReceiver public immutable voteSyncReceiver;
    /**
     * @notice A mapping keeps track of function signature associated with function name string.
     */
    mapping(bytes4 => string) public functionRegistry;

    /**
     * @notice Event emitted when function registry updated
     */
    event FunctionRegistryChanged(string signature, bool active);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(address voteSyncReceiver_) {
        ensureNonzeroAddress(voteSyncReceiver_);
        voteSyncReceiver = IVoteSyncReceiver(voteSyncReceiver_);
        _disableInitializers();
    }

    /**
     * @param accessControlManager_ Address of access control manager contract
     * @custom:error ZeroAddressNotAllowed is thrown when accessControlManager contract address is zero
     */
    function initialize(address accessControlManager_) external initializer {
        ensureNonzeroAddress(accessControlManager_);
        __AccessControlled_init(accessControlManager_);
    }

    /**
     * @notice Sets the trusted remote address for a specified remote chain ID
     * @param remoteChainId Chain Id of the remote chain
     * @param remoteAddress Address of the remote bridge
     * @custom:error ZeroAddressNotAllowed is thrown when remoteAddress contract address is zero
     * @custom:access Controlled by Access Control Manager
     */
    function setTrustedRemoteAddress(uint16 remoteChainId, bytes calldata remoteAddress) external {
        _checkAccessAllowed("setTrustedRemoteAddress(uint16,bytes)");
        require(remoteChainId != 0, "ChainId must not be zero");
        ensureNonzeroAddress(bytesToAddress(remoteAddress));
        voteSyncReceiver.setTrustedRemoteAddress(remoteChainId, remoteAddress);
    }

    /**
     * @notice Returns bool = true if srcAddress is trustedRemote corresponds to chainId_.
     * @param remoteChainId Chain Id of the remote chain.
     * @param remoteAddress Address of the remote bridge.
     * @custom:error ZeroAddressNotAllowed is thrown when remoteAddress contract address is zero.
     */
    function isTrustedRemote(uint16 remoteChainId, bytes calldata remoteAddress) external returns (bool) {
        require(remoteChainId != 0, "ChainId must not be zero");
        ensureNonzeroAddress(bytesToAddress(remoteAddress));
        return voteSyncReceiver.isTrustedRemote(remoteChainId, remoteAddress);
    }

    /**
     * @notice Invoked when called function does not exist in the contract
     * @param data Calldata containing the encoded function call
     * @return Result of function call
     * @custom:access Controlled by AccessControlManager
     */
    fallback(bytes calldata data) external returns (bytes memory) {
        string memory fun = _getFunctionName(msg.sig);
        require(bytes(fun).length != 0, "Function not found");
        _checkAccessAllowed(fun);
        (bool ok, bytes memory res) = address(voteSyncReceiver).call(data);
        require(ok, "call failed");
        return res;
    }

    /**
     * @notice A registry of functions that are allowed to be executed from proposals
     * @param signatures  Function signature to be added or removed
     * @param active Bool value, should be true to add function
     * @custom:event Emit FunctionRegistryChanged with signatures and its active bool value
     * @custom:access Only owner
     */
    function upsertSignature(string[] calldata signatures, bool[] calldata active) external onlyOwner {
        uint256 signatureLength = signatures.length;
        require(signatureLength == active.length, "Input arrays must have the same length");
        for (uint256 i; i < signatureLength; i++) {
            bytes4 sigHash = bytes4(keccak256(bytes(signatures[i])));
            bytes memory signature = bytes(functionRegistry[sigHash]);
            if (active[i] && signature.length == 0) {
                functionRegistry[sigHash] = signatures[i];
                emit FunctionRegistryChanged(signatures[i], true);
            } else if (!active[i] && signature.length != 0) {
                delete functionRegistry[sigHash];
                emit FunctionRegistryChanged(signatures[i], false);
            }
        }
    }

    /**
     * @notice This function transfer the ownership of the bridge from this contract to new owner
     * @param newOwner New owner of the XVS Bridge
     * @custom:access Controlled by AccessControlManager
     */
    function transferBridgeOwnership(address newOwner) external {
        _checkAccessAllowed("transferBridgeOwnership(address)");
        ensureNonzeroAddress(newOwner);
        voteSyncReceiver.transferOwnership(newOwner);
    }

    /**
     * @notice Empty implementation of renounce ownership to avoid any mishappening
     */
    function renounceOwnership() public override {}

    /**
     * @dev Returns function name string associated with function signature
     * @param signature Function signature
     */
    function _getFunctionName(bytes4 signature) internal view returns (string memory) {
        return functionRegistry[signature];
    }

    /**
     * @dev Converts bytes into address
     * @param data Data in bytes to be converted into address
     */
    function bytesToAddress(bytes calldata data) private pure returns (address) {
        return address(uint160(bytes20(data)));
    }
}

contracts/XVSVault/VotesSyncSender.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import { ensureNonzeroAddress } from "@venusprotocol/solidity-utilities/contracts/validators.sol";
import { Pausable } from "@openzeppelin/contracts/security/Pausable.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ILayerZeroEndpoint } from "@layerzerolabs/solidity-examples/contracts/lzApp/interfaces/ILayerZeroEndpoint.sol";
import { IAccessControlManagerV8 } from "@venusprotocol/governance-contracts/contracts/Governance/IAccessControlManagerV8.sol";

/**
 * @title VotesSyncSender
 * @author Venus
 * @notice The VotesSyncSender send voting information of user from remote chains(non-BSC chains) to Binance chain with the help of layer zero bridge.
 * It sends voting information of user from XVSVaultDest to receiver contract on Binance chain in the form of encoded data known as payload.
 */
contract VotesSyncSender is Ownable, Pausable, ReentrancyGuard {
    /**
     * @notice Remote chain id of binance
     */
    uint16 public immutable BSC_CHAIN_ID;

    /**
     * @notice Number of times votes synced
     */
    uint256 public votesSync;
    /**
     * @notice LayerZero endpoint for sending messages to remote chains
     */
    ILayerZeroEndpoint public LZ_ENDPOINT;

    /**
     * @notice Acess Control Manager
     */
    IAccessControlManagerV8 public accessControlManager;
    /**
     * @notice Execution hashes of failed messages
     * @dev [proposalId] -> [executionHash]
     */
    mapping(uint256 => bytes32) public storedExecutionHashes;
    /**
     * @notice Specifies the allowed path for sending messages (remote chainId => remote app address + local app address)
     */
    mapping(uint16 => bytes) public trustedRemoteLookup;

    /**
     * @notice Emitted when a remote message receiver is set for the remote chain
     */
    event SetTrustedRemoteAddress(uint16 indexed remoteChainId, bytes remoteAddress);

    /**
     * @notice Emitted when trusted remote sets to empty.
     */
    event TrustedRemoteRemoved(uint16 indexed chainId);

    /**
     * @notice Emitted when votes synced on BSC chain successfully
     */
    event ExecuteSyncVotes(uint16 remoteChainId, bytes payload, bytes adapterParams);

    /**
     * @notice Emitted when votes synced on retry
     */
    event ClearPayload(uint256, bytes32);

    /**
     * @notice Emitted when an execution hash of a failed message is saved
     */
    event StorePayload(
        uint256 votesSync,
        uint16 remoteChainId,
        bytes payload,
        bytes adapterParams,
        uint256 value,
        bytes reason
    );

    constructor(ILayerZeroEndpoint lzEndpoint_, IAccessControlManagerV8 accessControlManager_, uint16 dstChainId_) {
        ensureNonzeroAddress(address(lzEndpoint_));
        ensureNonzeroAddress(address(accessControlManager_));
        accessControlManager = accessControlManager_;
        LZ_ENDPOINT = lzEndpoint_;
        BSC_CHAIN_ID = dstChainId_;
    }

    /**
     * @notice Estimates LayerZero fees for cross-chain message delivery to the remote chain
     * @dev The estimated fees are the minimum required; it's recommended to increase the fees amount when sending a message. The unused amount will be refunded
     * @param payload The payload to be sent to the remote chain. It's computed as follows: payload = abi.encode(delegatee, checkpoint, votes)
     * @param adapterParams The params used to specify the custom amount of gas required for the execution on the destination
     * @return nativeFee The amount of fee in the native gas token (e.g. ETH)
     * @return zroFee The amount of fee in ZRO token
     */
    function estimateFee(bytes calldata payload, bytes calldata adapterParams) public view returns (uint256, uint256) {
        return LZ_ENDPOINT.estimateFees(BSC_CHAIN_ID, address(this), payload, false, adapterParams);
    }

    /**
     * @notice Sets the remote message receiver address
     * @param remoteAddress The address of the contract on the remote chain to receive messages sent by this contract
     * @custom:access Controlled by AccessControlManager.
     * @custom:event Emits SetTrustedRemoteAddress with remote chain Id and remote address
     */
    function setTrustedRemoteAddress(bytes calldata remoteAddress) external onlyOwner {
        ensureNonzeroAddress(address(uint160(bytes20(remoteAddress))));
        trustedRemoteLookup[BSC_CHAIN_ID] = abi.encodePacked(remoteAddress, address(this));
        emit SetTrustedRemoteAddress(BSC_CHAIN_ID, remoteAddress);
    }

    /**
     * @notice Remove trusted remote from storage.
     * @custom:access Only owner.
     * @custom:event Emit TrustedRemoteRemoved with remote chain Id.
     */
    function removeTrustedRemote() external onlyOwner {
        delete trustedRemoteLookup[BSC_CHAIN_ID];
        emit TrustedRemoteRemoved(BSC_CHAIN_ID);
    }

    /**
     * @notice Resends a previously failed message
     * @dev Allows providing more fees if needed. The extra fees will be refunded to the caller
     * @param votesSynced Number of votes synced to identify a failed message
     * @param payload The payload to be sent to the remote chain. It's computed as follows: payload = abi.encode(delegatee, checkpoint, votes)
     * @param adapterParams The params used to specify the custom amount of gas required for the execution on the Binance chain
     * @param originalValue The msg.value passed when syncVotes function was called
     * @custom:event Emits ClearPayload with votesSynced and hash
     * @custom:access Controlled by Access Control Manager
     */
    function retrySyncVotes(
        uint256 votesSynced,
        bytes calldata payload,
        bytes calldata adapterParams,
        uint256 originalValue
    ) external payable nonReentrant whenNotPaused {
        _ensureAllowed("retrySyncVotes(uint256,bytes,bytes,uint256)");
        bytes32 hash = storedExecutionHashes[votesSynced];
        require(hash != bytes32(0), "VotesSyncSender: no stored payload");
        require(payload.length != 0, "VotesSyncSender: empty payload");
        bytes memory trustedRemote = trustedRemoteLookup[BSC_CHAIN_ID];
        require(trustedRemote.length != 0, "VotesSyncSender: destination chain is not a trusted source");
        bytes memory execution = abi.encode(votesSynced, payload, adapterParams, originalValue);
        require(keccak256(execution) == hash, "VotesSyncSender: invalid execution params");

        delete storedExecutionHashes[votesSynced];

        LZ_ENDPOINT.send{ value: originalValue + msg.value }(
            BSC_CHAIN_ID,
            trustedRemoteLookup[BSC_CHAIN_ID],
            payload,
            payable(msg.sender),
            address(0),
            adapterParams
        );
        emit ClearPayload(votesSynced, hash);
    }

    /**
     * @notice Sync votes of user on Binance chain
     * @param payload  The payload to be sent to the remote chain. It's computed as follows: payload = abi.encode(delegatee, checkpoint, votes)
     * @param adapterParams The params used to specify the custom amount of gas required for the execution on the destination
     * @custom:event Emit ExecuteSyncVotes on success and StorePayload on failure
     * @custom:access Controlled by Access Control Manager
     */
    function syncVotes(bytes memory payload, bytes memory adapterParams) external payable whenNotPaused {
        _ensureAllowed("syncVotes(bytes,bytes)");
        require(payload.length != 0, "VotesSyncSender: Empty payload");
        bytes memory trustedRemote = trustedRemoteLookup[BSC_CHAIN_ID];
        require(trustedRemote.length != 0, "VotesSyncSender: destination chain is not a trusted source");
        votesSync++; // Number of times syncVotes is called

        try
            LZ_ENDPOINT.send{ value: msg.value }(
                BSC_CHAIN_ID,
                trustedRemote,
                payload,
                payable(tx.origin),
                address(0),
                adapterParams
            )
        {
            emit ExecuteSyncVotes(BSC_CHAIN_ID, payload, adapterParams);
        } catch (bytes memory reason) {
            storedExecutionHashes[votesSync] = keccak256(abi.encode(votesSync, payload, adapterParams, msg.value));
            emit StorePayload(votesSync, BSC_CHAIN_ID, payload, adapterParams, msg.value, reason);
        }
    }

    /**
     * @notice Ensure that the caller has permission to execute a specific function.
     * @param functionSig Function signature to be checked for permission.
     */
    function _ensureAllowed(string memory functionSig) internal view {
        require(
            IAccessControlManagerV8(accessControlManager).isAllowedToCall(msg.sender, functionSig),
            "access denied"
        );
    }
}
